import "./PriorityView.css";
import axios from "axios";
import { useEffect, useState } from "react";
import TaskListRow from "./TaskListRow";

export default function PriorityView() {
  const [lowPriorityTasks, setLowPriorityTasks] = useState([]);
  const [mediumPriorityTasks, setMediumPriorityTasks] = useState([]);
  const [highPriorityTasks, setHighPriorityTasks] = useState([]);
  const [updateTasks, setUpdateTasks] = useState(false);

  const getTasks = async () => {
    const { data } = await axios.get("http://localhost:3002/api/tasks");
    if (data.success === true && data.data.length > 0) {
      const lowPriority = data.data.filter((task) => task.priority === "Low");
      const mediumPriority = data.data.filter(
        (task) => task.priority === "Medium"
      );
      const highPriority = data.data.filter((task) => task.priority === "High");

      setLowPriorityTasks(lowPriority);
      setMediumPriorityTasks(mediumPriority);
      setHighPriorityTasks(highPriority);
    }
  };

  useEffect(() => {
    getTasks();
  }, [updateTasks]);

  const handleDrop = async (e, newPriority) => {
    e.preventDefault();
    const taskId = e.dataTransfer.getData("task_id");
    const currentPriority = e.dataTransfer.getData("task_priority");

    if (currentPriority !== newPriority) {
      const { data } = await axios.put(
        `http://localhost:3002/api/tasks/${taskId}`,
        {
          priority: newPriority,
        }
      );
      if (data.success) {
        setUpdateTasks(!updateTasks);
      }
    }
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div id="PriorityView">
      <h2>Priority View</h2>
      <div className="priority-tables">
        <div
          className="priority-table"
          onDrop={(e) => handleDrop(e, "Low")}
          onDragOver={allowDrop}
        >
          <h3>Low Priority</h3>
          <table>
            <thead></thead>
            <tbody>
              {lowPriorityTasks.map((task, index) => (
                <TaskListRow key={index} {...task} />
              ))}
            </tbody>
          </table>
        </div>
        <div
          className="priority-table"
          onDrop={(e) => handleDrop(e, "Medium")}
          onDragOver={allowDrop}
        >
          <h3>Medium Priority</h3>
          <table>
            <thead></thead>
            <tbody>
              {mediumPriorityTasks.map((task, index) => (
                <TaskListRow key={index} {...task} />
              ))}
            </tbody>
          </table>
        </div>
        <div
          className="priority-table"
          onDrop={(e) => handleDrop(e, "High")}
          onDragOver={allowDrop}
        >
          <h3>High Priority</h3>
          <table>
            <thead></thead>
            <tbody>
              {highPriorityTasks.map((task, index) => (
                <TaskListRow key={index} {...task} />
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
