import React, { useState, useEffect } from "react";
import styles from "./Login.css";
import { Link, useNavigate } from "react-router-dom";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { jwtDecode } from "jwt-decode";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [user, setUser] = useState({});

  const navigate = useNavigate();
  const auth = getAuth(); // Initialize Firebase authentication

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    const minLength = 8;
    const uppercaseRegex = /[A-Z]/;
    const numericRegex = /\d/;
    const specialCharRegex = /[!@#$%^&*()_+{}\[\]:;<>,.?~\\|]/;

    return (
      password.length >= minLength &&
      uppercaseRegex.test(password) &&
      numericRegex.test(password) &&
      specialCharRegex.test(password)
    );
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setEmailError("");
    setPasswordError("");

    let isValid = true;

    if (!email) {
      alert("Please enter your email.");
      isValid = false;
    } else if (!validateEmail(email)) {
      alert("Please enter a valid email address.");
      isValid = false;
    }

    if (!password) {
      alert("Please enter your password.");
      isValid = false;
    } else if (!validatePassword(password)) {
      alert(
        "Password must be at least 8 characters and contain at least one uppercase letter, one number, and one special character."
      );
      isValid = false;
    }

    if (isValid) {
      try {
        // Sign in user with email and password
        const userCredential = await signInWithEmailAndPassword(
          auth,
          email,
          password
        );
        const user = userCredential.user;
        alert("Login Successfull");
        navigate("/page");
        console.log("User logged in:", user);
        // You can redirect or perform other actions upon successful login
      } catch (error) {
        console.error("Error signing in:", error.message);
        // Handle authentication errors (e.g., display error message to user)
        alert("Invalid email or password. Please try again.");
      }
    }
  };

  useEffect(() => {
    const initializeGoogleSignIn = () => {
      // Wait for google object to become available
      const checkGoogleInterval = setInterval(() => {
        if (window.google && window.google.accounts) {
          clearInterval(checkGoogleInterval);
          initializeGoogle();
        }
      }, 100);
    };
    const initializeGoogle = () => {
      // Initialize Google Sign-In with custom button
      const customButton = document.getElementById("customSignInButton");

      if (customButton) {
        customButton.addEventListener("click", () => {
          window.google.accounts.id.prompt();
        });

        window.google.accounts.id.initialize({
          client_id:
            "483808031047-nsuuol0b07asnqocbdt5clacqp3j8fhu.apps.googleusercontent.com",
          callback: handleCallbackResponse,
        });
      }
    };

    initializeGoogleSignIn();
  }, []);

  const handleCallbackResponse = (response) => {
    console.log("Encoded JWT ID token " + response.credential);
    var userObject = jwtDecode(response.credential);
    console.log(userObject);
    setUser(userObject);
    alert("Login Successful");
    navigate("/page", { state: { user: userObject } });
  };

  return (
    <div className={styles.container}>
      <div className={styles.leftContainer}>
        <div className={styles.textContainer}>
          <h2>Haven't registered in our app yet?</h2>
        </div>
        <div className={styles.image}>
          <img src="./Images/Login/QRscan.png" alt="QR Code" />
        </div>
        <div className={styles.textContainer2}>
          <h2>Register in our app to sign in to the dashboard</h2>
          <h2>and access all its features.</h2>
        </div>
      </div>

      <div className={styles.rightContainer}>
        <div className={styles.subcontainer}>
          <img
            className={styles.logo}
            src="./Images/Login/logo2.png"
            alt="Review Lite logo"
          />
          <h1>Sign in</h1>
          <div className={styles.inputs}>
            <div>
              <img
                src="./Images/Login/Email.png"
                alt="Email Icon"
                className={styles.icon}
              />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {emailError && <span className="error-text">{emailError}</span>}
            </div>
            <div>
              <img
                src="./Images/Login/PrivateLock.png"
                alt="Email Icon"
                className={styles.icon}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              {passwordError && (
                <span className="error-text">{passwordError}</span>
              )}
            </div>
          </div>
          <button className={styles.signin} onClick={handleLogin}>
            Sign in
          </button>
          <h4 className={styles.horizontalLine}>or</h4>

          <div>
            <img
              src="./Images/Login/Google.png"
              alt="Google Icon"
              className={styles.Gicon}
            />
            <button id="customSignInButton" className={styles.googlebutton}>
              Sign in with Google
            </button>
          </div>

          <Link className={styles.forgotPassword} to="/forgotPassword">
            Forgot Password?
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
